export type Listener = (...args: any[]) => void;

export class EventEmitter {
  public static defaultMaxListeners: number;
  public static listenerCount(emitter: EventEmitter, type: string | number): number;

  public eventNames(): Array<string | number>;
  public setMaxListeners(n: number): this;
  public getMaxListeners(): number;
  public emit(type: string | number, ...args: any[]): boolean;
  public addListener(type: string | number, listener: Listener): this;
  public on(type: string | number, listener: Listener): this;
  public once(type: string | number, listener: Listener): this;
  public prependListener(type: string | number, listener: Listener): this;
  public prependOnceListener(type: string | number, listener: Listener): this;
  public removeListener(type: string | number, listener: Listener): this;
  public off(type: string | number, listener: Listener): this;
  public removeAllListeners(type?: string | number): this;
  public listeners(type: string | number): Listener[];
  public listenerCount(type: string | number): number;
  public rawListeners(type: string | number): Listener[];
}
